import React from "react";
import { differenceInDays } from "date-fns";

const Task = ({ taskObj, onComplete }) => {
  let deadlineText = "";

  let remainingDays = differenceInDays(new Date(taskObj.deadline), new Date());
  const handleRemainingDays = () => {
    if (remainingDays > 0) {
      deadlineText += `${remainingDays} gün sonra`;
    }
  };

  return (
    <div className="p-6 bg-white rounded-md leading-normal my-4 shadow-md">
      <h3 className="text-lg">{taskObj.title}</h3>
      <div className="text-xs pt-1">
        son teslim: <span>{handleRemainingDays()} gün sonra</span>
      </div>
      <p className="px-2 text-sm text-stone-700">{taskObj.description}</p>
      <div>
        {taskObj.people.map((p) => (
          <span className="pill" key={p}>
            {p}
          </span>
        ))}
      </div>
      {onComplete && (
        <button onClick={() => onComplete(taskObj.id)}>Tamamlandı</button>
      )}
    </div>
  );
};

export default Task;
